import React, { createContext, useContext, useEffect, useState } from "react";
const QuizContext = createContext();

function Context({ children }) {
  const [questionList, setQuestionList] = useState([]);
  const [selectedQuestion, setSelectedQuestion] = useState(0);
  const [options, setOptions] = useState([]);
  const [numberOfQuestion, setNumberOfQuestion] = useState(0);
  const [correctAnswer, setCorrectAnswer] = useState(0);
  const [selectedOption, setSelectedOption] = useState(false);
  const [wrongAnsSelect, setWrongAnsSelect] = useState(false);
  const [attemptedQuestion, setAttemptedQuestion] = useState(0);

  const fetchData = async () => {
    try {
      const url = "https://opentdb.com/api.php?amount=15&category=21";
      const data = await fetch(url);
      if (data.status === 200) {
        const res = await data.json();
        setQuestionList(res?.results);
        setNumberOfQuestion(res?.results?.length);
        const randomOption = () => {
          const seriesOption = [
            res.results[0]?.correct_answer,
            ...res.results[0]?.incorrect_answers,
          ];
          setOptions(
            seriesOption
              .map((answer) => ({ id: Math.random(), value: answer }))
              .sort((a, b) => a.id - b.id)
          );
        };
        randomOption();
      }
    } catch (err) {
      alert("Something went wrong");
    }
  };

  useEffect(() => {
    fetchData();
  }, []);
  return (
    <QuizContext.Provider
      value={{
        questionList,
        setQuestionList,
        selectedQuestion,
        setSelectedQuestion,
        options,
        setOptions,
        numberOfQuestion,
        correctAnswer,
        setCorrectAnswer,
        selectedOption,
        setSelectedOption,
        wrongAnsSelect,
        setWrongAnsSelect,
        attemptedQuestion,
        setAttemptedQuestion,
      }}
    >
      {children}
    </QuizContext.Provider>
  );
}
export const StateValue = () => {
  return useContext(QuizContext);
};

export default Context;
